    "primaryAxisSizingMode": "AUTO", "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "component",
        "id": "10:7773",
        "variants": {
          "Photo": "Yes"
        },
        "horizontalSizing": "FILL"
      },
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "textLayers": {
          "label-text": "Name"
        },
        "horizontalSizing": "FILL"
      },
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "textLayers": {
          "label-text": "Email"
        },
        "horizontalSizing": "FILL"
      },
      {
