  "layoutContainer": {
    "name": "Add Address Screen",
    "layoutMode": "VERTICAL",
    "itemSpacing": 16,
    "paddingTop": 16, "paddingBottom": 16, "paddingLeft": 16, "paddingRight": 16,
    "primaryAxisSizingMode": "AUTO", "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "layoutContainer",
        "layoutMode": "VERTICAL",
        "items": [
          {
            "type": "component",
            "id": "10:8492",
            "variants": {
              "Type": "Filled",
              "State": "enabled"
            },
            "properties": {
              "label-text": "Street Address",
              "placeholder-text": "Enter street address"
            }
          },
          {
            "type": "component",
            "id": "10:8492",
            "variants": {
