    "paddingRight": 16,
    "primaryAxisSizingMode": "AUTO",
    "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "properties": {
          "placeholder-text": "Email Address",
          "horizontalSizing": "FILL"
        }
      },
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
