  "layoutContainer": {
    "name": "Login Screen",
    "layoutMode": "VERTICAL",
    "itemSpacing": 16,
    "paddingTop": 32, "paddingBottom": 32, "paddingLeft": 16, "paddingRight": 16,
    "primaryAxisSizingMode": "AUTO", "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "properties": {
          "placeholder-text": "Email"
        },
        "horizontalSizing": "FILL"
      },
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "properties": {
          "placeholder-text": "Password"
        },
        "horizontalSizing": "FILL"
      },
      {
        "type": "component",
        "id": "10:3907",
        "variants": {
          "Platform": "Android+Web",
          "Size": "Medium",
          "Type": "Filled",
          "State": "Default",
          "Color": "Green"
        },
        "properties": {
          "Action": "Login"
        },
        "horizontalSizing": "FILL"
      },
      {
        "type": "native-text",
        "properties": {
          "content": "Forgot Password?",
          "textStyle": "Body/Small",
          "color": "Secondary/secondary50",
