  "layoutContainer": {
    "name": "Product Listing Page",
    "layoutMode": "VERTICAL",
    "itemSpacing": 16,
    "paddingTop": 16, "paddingBottom": 16, "paddingLeft": 16, "paddingRight": 16,
    "primaryAxisSizingMode": "AUTO", "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "layoutContainer",
        "layoutMode": "HORIZONTAL",
        "itemSpacing": 16,
        "items": [
          {
            "type": "component",
            "id": "10:12086",
            "variants": {
              "Device": "Mobile",
              "State": "default"
            },
            "horizontalSizing": "FILL"
          },
          {
            "type": "component",
            "id": "10:3907",
            "variants": {
              "Platform": "Android+Web",
              "Size": "Medium",
              "Type": "Text",
              "State": "Default",
              "Color": "Green"
            }
          }
        ]
      },
      {
        "type": "layoutContainer",
        "layoutMode": "HORIZONTAL",
        "layoutWrap": "WRAP",
        "itemSpacing": 8,
        "items": [
          {
            "type": "component",
            "id": "147:3743",
            "variants": {
              "view": "grid",
              "type": "normal"
            },
            "width": 170
          },
          {
