  "layoutContainer": {
    "name": "Product Listing Page",
    "layoutMode": "VERTICAL",
    "itemSpacing": 16,
    "paddingTop": 0, "paddingBottom": 0, "paddingLeft": 0, "paddingRight": 0,
    "primaryAxisSizingMode": "AUTO", "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "component",
        "id": "10:5620",
        "variants": {
          "Mode": "Light",
          "Type": "Default",
          "Elevation": "flat"
        },
        "properties": {
          "headline": "Search Results",
          "subheadline": "Electronics"
        }
      },
      {
        "type": "layoutContainer",
        "layoutMode": "HORIZONTAL",
        "itemSpacing": 16,
        "paddingLeft": 16,
        "paddingRight": 16,
        "items": [
          {
            "type": "component",
            "id": "10:12086",
            "variants": {
              "Device": "Mobile",
              "State": "default"
            },
            "properties": {
              "Search in %Category%": "Search Electronics"
            }
          },
          {
            "type": "component",
            "id": "10:3907",
            "variants": {
              "Platform": "Android+Web",
              "Size": "Medium",
              "Type": "Filled",
              "State": "Default",
              "Color": "Green"
            },
            "properties": {
              "Action": "Filter"
            }
          }
        ]
      },
      {
        "type": "layoutContainer",
        "layoutMode": "HORIZONTAL",
        "layoutWrap": "WRAP",
        "itemSpacing": 8,
        "paddingTop": 16, "paddingRight": 16, "paddingBottom": 16, "paddingLeft": 16,
        "items": [
          {
            "type": "component",
            "id": "147:3743",
            "variants": {
              "view": "grid",
              "type": "normal"
            },
            "properties": {
              "#title": "Product Title 1",
              "#price": "₦10,000",
              "#attribute1 value": "4.5",
              "#attribute1 value": "(47)"
            }
          },
          {
            "type": "component",
            "id": "147:3743",
            "variants": {
              "view": "grid",
              "type": "normal"
            },
            "properties": {
              "#title": "Product Title 2",
              "#price": "₦25,000",
              "#attribute1 value": "3.8",
              "#attribute1 value": "(12)"
            }
          }
          // ...more product cards...
        ]
      }
