  "layoutContainer": {
    "name": "Login Screen",
    "layoutMode": "VERTICAL",
    "itemSpacing": 16,
    "paddingTop": 32, "paddingBottom": 32, "paddingLeft": 16, "paddingRight": 16,
    "primaryAxisSizingMode": "AUTO", "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "properties": {
          "placeholder-text": "Email Address",
          "horizontalSizing": "FILL"
        }
      },
      {
        "type": "layoutContainer",
        "layoutMode": "HORIZONTAL",
        "itemSpacing": 8,
        "items": [
          {
            "type": "component",
            "id": "10:8492",
            "variants": {
              "Type": "Filled",
              "State": "enabled"
            },
            "properties": {
              "placeholder-text": "Password",
              "horizontalSizing": "FILL"
            }
          },
          {
            "type": "component",
