  "layoutContainer": {
    "name": "Login Screen",
    "layoutMode": "VERTICAL",
    "itemSpacing": 16,
    "paddingTop": 32,
    "paddingBottom": 32,
    "paddingLeft": 16,
    "paddingRight": 16,
    "primaryAxisSizingMode": "AUTO",
    "counterAxisSizingMode": "AUTO",
    "items": [
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "properties": {
          "label-text": "Email Address",
          "placeholder-text": "Enter your email"
        }
      },
      {
        "type": "component",
        "id": "10:8492",
        "variants": {
          "Type": "Filled",
          "State": "enabled"
        },
        "properties": {
          "label-text": "Password",
          "placeholder-text": "Enter your password",
          "isPassword": true
        }
      },
      {
        "type": "component",
        "id": "10:3907",
        "variants": {
          "Platform": "Android+Web",
          "Size": "Medium",
